    def record(self,text):
        import sqlite3
        customer_name=self.records_edit.text()
        connection=sqlite3.connect('Resources/DataFiles/booking_data.db')
        result=connection.execute('''SELECT * FROM booking WHERE consumer_name= '{}' '''.format(customer_name))
        self.list1.setRowCount(0)
        for row_number,row_data in enumerate(result):
            self.list1.insertRow(row_number)
            for column_number,data in enumerate(row_data):
                self.list1.setItem(row_number,column_number,QtWidgets.QTableWidgetItem(str(data)))
        connection.close()
        

        
        
    def payment(self,text):
        import sqlite3
        consumer_id=self.cdrid.text()
        connection1=sqlite3.connect('Resources/DataFiles/booking_data.db')
        result1=connection1.execute('''SELECT * FROM payment WHERE booking_id='{}' '''.format(consumer_id))
        self.list2.setRowCount(0)
        for row_number,row_data in enumerate(result1):
            self.list2.insertRow(row_number)
            for column_number,data in enumerate(row_data):
                self.list2.setItem(row_number,column_number,QtWidgets.QTableWidgetItem(str(data)))
        connection1.close()

    def delete(self,text):
        consumer_id=self.cdrid.text()
        import sqlite3
        conn = sqlite3.connect('Resources/DataFiles/booking_data.db')
        curconn=conn.cursor()
        curconn.execute('''DELETE FROM booking WHERE id= '{}' '''.format(consumer_id))
        conn.commit()
        curconn.execute('''DELETE FROM payment WHERE booking_id= '{}' '''.format(consumer_id))
        conn.commit()
        self.del_edit.setText('Deleted!!')
        curconn.close()


